import React, { useState } from 'react';
import './CartItem.css';

const CartItem = ({ item, onCartUpdate, onRemove }) => {
    const { id, hotelName, hotelImage, hotelDescription, nights, people, price, maxNights, maxPeople } = item;

    const [editedNights, setEditedNights] = useState(nights);
    const [editedPeople, setEditedPeople] = useState(people);

    const handleUpdate = () => {
        if (editedNights > 0 && editedPeople > 0) {
            onCartUpdate(id, editedNights, editedPeople); // Викликаємо функцію оновлення
        } else {
            alert('Кількість ночей та людей має бути більше 0.');
        }
    };

    const handleNightsChange = (e) => {
        setEditedNights(Number(e.target.value)); // Оновлюємо стан кількості ночей
    };

    const handlePeopleChange = (e) => {
        setEditedPeople(Number(e.target.value)); // Оновлюємо стан кількості людей
    };

    return (
        <div className="cart-item">
            <img 
                src={`/path/to/images/${hotelImage}`} 
                alt={hotelName} 
                className="item-image" 
            />
            <div className="item-details">
                <h3>{hotelName}</h3>
                <p>{hotelDescription}</p>

                <label>
                    Кількість ночей:
                    <select value={editedNights} onChange={handleNightsChange}>
                        {Array.from({ length: maxNights }, (_, index) => (
                            <option key={index + 1} value={index + 1}>{index + 1}</option>
                        ))}
                    </select>
                </label>

                <label>
                    Кількість людей:
                    <select value={editedPeople} onChange={handlePeopleChange}>
                        {Array.from({ length: maxPeople }, (_, index) => (
                            <option key={index + 1} value={index + 1}>{index + 1}</option>
                        ))}
                    </select>
                </label>
            </div>

            <div className="item-total">
                Загальна Вартість: {price} ГРН
            </div>

            <button onClick={handleUpdate} className="update-item-button">
                Оновити
            </button>
            <button onClick={() => onRemove(id)} className="remove-item-button">
                Видалити
            </button>
        </div>
    );
};

export default CartItem;
