import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';


function HotelCard() {
  const { id } = useParams(); // Отримуємо id з URL за допомогою useParams
  const [hotel, setHotel] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchHotel = async () => {
      setLoading(true);
      try {
        const response = await axios.get(`http://localhost:5000/api/hotels/${id}`);
        setHotel(response.data);
      } catch (error) {
        setError('Помилка при завантаженні даних готелю');
      } finally {
        setLoading(false);
      }
    };

    fetchHotel();
  }, [id]);

  if (loading) return <p>{error}</p>;
  if (error) return <p>{error}</p>;
  if (!hotel) return <p>Готель не знайдено</p>;

  const { name, visitors, rooms, image, price } = hotel;

  return (
    <div className="hotel-card">
      <img src={`/images/${image}`} alt={name} className="hotel-image" />
      <h3>{name}</h3>
      <p><strong>Кількість відвідувачів:</strong> {visitors}</p>
      <p><strong>Кількість номерів:</strong> {rooms}</p>
      <p><strong>Ціна:</strong> {price} грн / за добу</p>
    </div>
  );
}

export default HotelCard;
