// HotelDetails.js
import React from 'react';
import { useParams } from 'react-router-dom';
import hotelsData from '../../data/Hotels.json';

const HotelDetails = () => {
    const { id } = useParams();
    const hotel = hotelsData.find((hotel) => hotel.id === parseInt(id, 10));
    console.log("Hotel data:", hotel); // Діагностика для перевірки даних готелю

    if (!hotel) {
        return <div>Готель не знайдено.</div>;
    }

    return (
        <div>
            <h1>{hotel.name}</h1>
            <p><strong>Опис:</strong> {hotel.description}</p>
            <p><strong>Кількість відвідувачів:</strong> {hotel.visitors}</p>
            <p><strong>Кількість номерів:</strong> {hotel.rooms}</p>
            <p><strong>Ціна:</strong> {hotel.price} грн / за добу</p>
        </div>
    );
};

export default HotelDetails;
