import React, { useState, useEffect } from 'react';
import './Grid.css'; 
import hotelsData from '../../data/hotels.json';
import rixos from '../../assets/images/rixos.jpg';
import Hilton from '../../assets/images/hilton.JPEG';
import Bukovel from '../../assets/images/bukovel.jpg';
import HotelCard from './HotelCard/HotelCard';

const images = {
    "rixos.jpg": rixos,
    "hilton.JPEG": Hilton,
    "bukovel.jpg": Bukovel
};

function Grid() {
    const [hotels, setHotels] = useState([]);

    useEffect(() => {
        setHotels(hotelsData);
    }, []);

    return (
        <div className="grid">
            {hotels.map((hotel, index) => (
                <HotelCard
                    key={hotel.id}
                    name={hotel.name}
                    visitors={hotel.visitors}
                    rooms={hotel.rooms}
                    image={images[hotel.image]}
                    price={hotel.price}
                    description={hotel.description}
                />
            ))}
            <button className="view-more">Переглянути всі</button>
        </div>
    );
}

export default Grid;
