import React, { useEffect, useState } from "react";
import './CartMain.css';
import api from '../../api/axiosConfig'; // Використовуйте один екземпляр API
import CartItem from '../CartItem/CartItem'; // Імпорт компоненту CartItem
import CartSummary from '../CartSummary/CartSummary'; // Імпорт компоненту CartSummary
import { useNavigate } from 'react-router-dom'; // Для повернення до каталогу

const Cart = () => {
    const [cart, setCart] = useState([]); // Початковий стан — пустий масив
    const [isLoading, setIsLoading] = useState(true); // Стан завантаження
    const [error, setError] = useState(null); // Для повідомлень про помилки
    const navigate = useNavigate(); // Для кнопки повернення до каталогу

    // Завантаження кошика
    const loadCart = async () => {
        try {
            setIsLoading(true);
            const response = await api.get('/cart'); // Виклик API для отримання кошика
            console.log('Отримані дані кошика:', response.data); // Лог для перевірки
            setCart(response.data); // Оновлюємо стан напряму
            setError(null); // Очищаємо помилки, якщо запит вдався
        } catch (err) {
            console.error('Помилка завантаження кошика:', err);
            setError('Не вдалося завантажити кошик. Спробуйте пізніше.');
        } finally {
            setIsLoading(false);
        }
    };

    useEffect(() => {
        loadCart(); // Завантажуємо кошик при першому рендері
    }, []);

    useEffect(() => {
        console.log('Стан кошика:', cart); // Лог для діагностики стану
    }, [cart]);

    // Видалення елемента з кошика
    const handleRemove = async (itemId) => {
        try {
            await api.delete(`/cart/${itemId}`); // Виклик API для видалення
            loadCart(); // Оновлюємо кошик після видалення
        } catch (err) {
            console.error('Помилка видалення елемента кошика:', err);
            alert('Не вдалося видалити елемент. Спробуйте знову.');
        }
    };

    // Оновлення кількості ночей і людей
    const handleUpdate = async (itemId, nights, people) => {
        try {
            await api.put(`/cart/${itemId}`, { nights, people }); // Виклик API для оновлення
            loadCart(); // Оновлюємо кошик після оновлення
        } catch (err) {
            console.error('Помилка оновлення елемента кошика:', err);
            alert('Не вдалося оновити елемент. Спробуйте знову.');
        }
    };

    // Розрахунок загальної вартості
    const totalPrice = cart.reduce((sum, item) => sum + parseFloat(item.price), 0);

    // Обробка замовлення
    const handleOrder = () => {
        navigate('/checkout'); // Повернення до каталогу після замовлення
    };

    return (
        <div className="cart-page">
            <h1>Ваш кошик</h1>
            {isLoading ? (
                <p>Завантаження кошика...</p>
            ) : error ? (
                <p className="error-message">{error}</p>
            ) : (
                <>
                    <div className="cart-items">
                        {cart.length > 0 ? (
                            cart.map((item) => (
                                <CartItem 
                                    key={item.id} 
                                    item={item} 
                                    onCartUpdate={handleUpdate} 
                                    onRemove={handleRemove} 
                                />
                            ))
                        ) : (
                            <p className="cart-empty-message">Кошик порожній</p>
                        )}
                    </div>
                    {cart.length > 0 && (
                        <>
                            <CartSummary cart={cart} />
                            <div className="cart-actions">
                                <button onClick={() => navigate('/catalog')} className="go-back-button">
                                    Повернутись до каталогу
                                </button>
                                <button onClick={handleOrder} className="checkout-button">
                                    Оформити замовлення
                                </button>
                            </div>
                        </>
                    )}
                </>
            )}
        </div>
    );
};

export default Cart;
