import React, { useState, useEffect } from 'react';
import './Grid.css'; 
import hotelsData from '../../data/Hotels.json';
import HotelCard from '../HotelsList/HotelCard/HotelCard';
import Filters from '../Filters/Filters';

function Grid() {
    const [hotels, setHotels] = useState([]);
    const [filters, setFilters] = useState({ price: '', visitors: '', rooms: '' });
    const [showAll, setShowAll] = useState(false);

    useEffect(() => {
        setHotels(hotelsData);
    }, []);

    const handleFilterChange = (selectedFilters) => {
        setFilters(selectedFilters);
    };

    const filteredHotels = hotels.filter((hotel) => {
        const priceMatch = filters.price ? hotel.price <= Number(filters.price) : true;
        const visitorsMatch = filters.visitors ? hotel.visitors <= Number(filters.visitors) : true;
        const roomsMatch = filters.rooms ? hotel.rooms <= Number(filters.rooms) : true;
        return priceMatch && visitorsMatch && roomsMatch;
    });

    const visibleHotels = showAll ? filteredHotels : filteredHotels.slice(0, 2);

    const handleToggle = () => {
        setShowAll(!showAll);
    };

    return (
        <div className="grid">
            <Filters onFilterChange={handleFilterChange} />
            {visibleHotels.map((hotel) => (
                <HotelCard
                    key={hotel.id}
                    name={hotel.name}
                    visitors={hotel.visitors}
                    rooms={hotel.rooms}
                    image={hotel.image}
                    price={hotel.price}
                    description={hotel.description}
                />
            ))}
            <button className="view-more" onClick={handleToggle}>
                {showAll ? 'Приховати' : 'Переглянути всі'}
            </button>
        </div>
    );
}

export default Grid;
