// HotelsList.js
import React, { useState, useEffect } from 'react';
import HotelCard from './HotelCard/HotelCard';
import Filters from '../Filters/Filters';

function HotelsList() {
    const [hotels, setHotels] = useState([]);
    const [filters, setFilters] = useState({ price: '', visitors: '', rooms: '' });
    const [loading, setLoading] = useState(true);

    // Функція для завантаження готелів з урахуванням фільтрів
    const fetchHotels = async () => {
        setLoading(true);
        const query = new URLSearchParams(filters).toString();
        try {
            const response = await fetch(`http://localhost:5000/api/hotels?${query}`);
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            console.log('Received data:', data); // Логування для перевірки результатів
            setHotels(data);
        } catch (error) {
            console.error('Error fetching hotels:', error);
        } finally {
            setLoading(false);
        }
    };

    // Викликаємо fetchHotels кожного разу, коли змінюються фільтри
    useEffect(() => {
        fetchHotels();
    }, [filters]);

    // Обробка зміни фільтрів
    const handleFilterChange = (newFilters) => {
        setFilters(newFilters);
    };

    return (
        <div>
            <Filters onFilterChange={handleFilterChange} />
            {loading ? (
                <div>Loading...</div>
            ) : (
                <div className="hotels-list">
                    {hotels.length > 0 ? (
                        hotels.map((hotel) => (
                            <HotelCard
                                key={hotel.id}
                                name={hotel.name}
                                image={hotel.image}
                                description={hotel.description}
                            />
                        ))
                    ) : (
                        <div>Готель не знайдено</div>
                    )}
                </div>
            )}
        </div>
    );
}

export default HotelsList;
