import React, { useState, useEffect } from 'react';
import './HotelCard.css';
import PrimaryButton from '../../PrimaryButton/PrimaryButton';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Loader from '../../Loader/Loader';

function HotelCard({ id }) {
  const navigate = useNavigate();
  const [hotel, setHotel] = useState(null); // Дані готелю
  const [loading, setLoading] = useState(true); // Стан завантаження
  const [error, setError] = useState(null); // Стан помилки

  useEffect(() => {
    const fetchHotel = async () => {
      setLoading(true);
      try {
        const response = await axios.get(`http://localhost:3000/api/hotels/${id}`);
        setHotel(response.data);
      } catch (error) {
        setError(error.message || 'Не вдалося завантажити дані');
      } finally {
        setLoading(false);
      }
    };

    fetchHotel();
  }, [id]);

  if (loading) return <Loader />;
  if (error) return <p>{error}</p>;
  if (!hotel) return <p>Готель не знайдено</p>;

  const { name, visitors, rooms, image, price } = hotel;
  const handleNavigate = () => {
    navigate(`/hotel/${id}`);
  };

  const imagePath = require(`../../../assets/images/${image}`);

  return (
    <div className="hotel-card">
      <img src={imagePath} alt={name} className="hotel-image" />
      <h3>{name}</h3>
      <p><strong>Кількість відвідувачів:</strong> {visitors}</p>
      <p><strong>Кількість номерів:</strong> {rooms}</p>
      <p><strong>Ціна:</strong> {price} грн / за добу</p>

      <PrimaryButton label="Дізнатись більше" onClick={handleNavigate} />
    </div>
  );
}

export default HotelCard;
