import React from 'react';
import './CartItem.css'; // Додайте свої стилі, якщо є

const CartItem = ({ item, onCartUpdate }) => {
    const { id, hotelName, hotelImage, hotelDescription, nights, people, price } = item;

    return (
        <div className="cart-item">
            <img 
                src={`/path/to/images/${hotelImage}`} 
                alt={hotelName} 
                className="cart-item-image" 
            />
            <h3>{hotelName}</h3>
            <p>{hotelDescription}</p>
            <p>Кількість ночей: {nights}</p>
            <p>Кількість людей: {people}</p>
            <p>Ціна: {price} грн</p>
            <button
                onClick={() => onCartUpdate(id)} // Виклик видалення
                className="remove-item-button"
            >
                Видалити
            </button>
        </div>
    );
};

export default CartItem;
