import React, { useState, useEffect } from 'react';
import HotelCard from './HotelCard/HotelCard';
import Filters from '../Filters/Filters';
import Loader from '../Loader/Loader';
import { getProducts } from '../../api/axiosConfig';
import './HotelsList.css';

function HotelsList() {
    const [hotels, setHotels] = useState([]);
    const [filters, setFilters] = useState({ price: '', visitors: '', rooms: '' });
    const [searchTerm, setSearchTerm] = useState(''); // Окрема стрічка для пошуку
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Функція для завантаження готелів
    const fetchHotels = async (filters, searchTerm = '') => {
        setLoading(true);
        try {
            const response = await getProducts({ ...filters, name: searchTerm }); 
            setHotels(response.data);
            setLoading(false);
        } catch (error) {
            setError(error.message);
            setLoading(false);
        }
    };

    // Викликаємо `fetchHotels` при зміні фільтрів
    useEffect(() => {
        fetchHotels(filters, searchTerm);
    }, [filters, searchTerm]);

    // Функція для обробки змін у фільтрах
    const handleFilterChange = (newFilters) => {
        setFilters(newFilters);
    };

    // Функція для обробки введення в пошукову стрічку
    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
    };

    return (
        <div>
            <input
                type="text"
                placeholder="Пошук за назвою готелю"
                value={searchTerm}
                onChange={handleSearchChange}
                className="search-bar"
            />
            <Filters onFilterChange={handleFilterChange} />
            {loading ? (
                <Loader />
            ) : error ? (
                <div>Error: {error}</div>
            ) : (
                <div className="hotels-list">
                    {hotels.length > 0 ? (
                        hotels.map((hotel) => (
                            <HotelCard
                                key={hotel.id}
                                name={hotel.name}
                                price={hotel.price}
                                rooms={hotel.rooms}
                                visitors={hotel.visitors}
                                image={hotel.image}
                                description={hotel.description}
                            />
                        ))
                    ) : (
                        <div>Готель не знайдено</div>
                    )}
                </div>
            )}
        </div>
    );
}

export default HotelsList;
