import React, { useEffect, useState } from 'react';
import api from '../../api/axiosConfig'; // Використовуйте один екземпляр API
import CartItem from '../CartItem/CartItem';
import CartSummary from '../CartSummary/CartSummary';
import './CartMain.css';

const Cart = () => {
    const [cart, setCart] = useState([]); // Початковий стан — пустий масив
    const [isLoading, setIsLoading] = useState(true); // Стан завантаження
    const [error, setError] = useState(null); // Для повідомлень про помилки

    // Завантаження кошика
    const loadCart = async () => {
        try {
            setIsLoading(true);
            const response = await api.get('/cart'); // Виклик API для отримання кошика
            console.log('Отримані дані кошика:', response.data); // Лог для перевірки
            setCart(response.data); // Оновлюємо стан напряму
            setError(null); // Очищаємо помилки, якщо запит вдався
        } catch (err) {
            console.error('Помилка завантаження кошика:', err);
            setError('Не вдалося завантажити кошик. Спробуйте пізніше.');
        } finally {
            setIsLoading(false);
        }
    };

    useEffect(() => {
        loadCart(); // Завантажуємо кошик при першому рендері
    }, []);

    useEffect(() => {
        console.log('Стан кошика:', cart); // Лог для діагностики стану
    }, [cart]);

    // Видалення елемента з кошика
    const handleRemove = async (itemId) => {
        try {
            await api.delete(`/cart/${itemId}`); // Виклик API для видалення
            loadCart(); // Оновлюємо кошик після видалення
        } catch (err) {
            console.error('Помилка видалення елемента кошика:', err);
            alert('Не вдалося видалити елемент. Спробуйте знову.');
        }
    };

    // Розрахунок загальної вартості
    const totalPrice = cart.reduce((sum, item) => sum + parseFloat(item.price), 0);


    return (
        <div className="cart-page">
            <h1>Ваш кошик</h1>
            {isLoading ? (
                <p>Завантаження кошика...</p>
            ) : error ? (
                <p className="error-message">{error}</p>
            ) : (
                <>
                    <div className="cart-items-list">
                        {cart.items.length > 0 ? (
                            cart.items.map((item) => (
                                <CartItem key={item.id} item={item} onCartUpdate={loadCart} />
                            ))
                        ) : (
                            <p className="cart-empty-message">Кошик порожній</p>
                        )}
                    </div>
                    <CartSummary cart={cart.items} /> {/* Передаємо `cart.items`, а не `cart` */}
                    {cart.items.length > 0 && (
                        <button className="checkout-button">Завершити покупку</button>
                    )}
                </>
            )}
            <button onClick={handleUpdateCart} className="update-cart-button">
                Оновити кошик
            </button>
        </div>
    );
};

export default Cart;
