import React, { useEffect, useState } from "react";
import './Info.css';
import { useParams, useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { addToCart } from '../../redux/actions';
import { addToCartToServer, getHotelDetails } from '../../api/axiosConfig';

const ItemPage = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const dispatch = useDispatch();

    const [hotel, setHotel] = useState(null);
    const [nights, setNights] = useState(1);
    const [people, setPeople] = useState(1);
    const [error, setError] = useState('');

    // Завантаження інформації про готель з обмеженнями
    const loadHotelDetails = async () => {
        try {
            const data = await getHotelDetails(id);
            setHotel(data);
        } catch (error) {
            console.error("Помилка завантаження готелю:", error);
        }
    };

    useEffect(() => {
        loadHotelDetails();
    }, [id]);

    if (!hotel) {
        return <div>Готель не знайдено.</div>;
    }

    const handleNightsChange = (e) => {
        const value = Number(e.target.value);
        if (value > hotel.maxNights) {
            setError(`Максимальна кількість ночей для цього готелю - ${hotel.maxNights}`);
        } else {
            setError('');
            setNights(value);
        }
    };

    const handlePeopleChange = (e) => {
        const value = Number(e.target.value);
        if (value > hotel.maxPeople) {
            setError(`Максимальна кількість осіб для цього готелю - ${hotel.maxPeople}`);
        } else {
            setError('');
            setPeople(value);
        }
    };

    const handleAddToCart = async () => {
        if (nights > hotel.maxNights || people > hotel.maxPeople) {
            setError('Ви перевищили допустиму кількість.');
            return;
        }

        const hotelWithDetails = { ...hotel, nights, people, totalPrice: hotel.price * nights * people };
        try {
            await addToCartToServer(hotelWithDetails);
            dispatch(addToCart(hotelWithDetails));
            alert('Товар додано до кошика');
        } catch (error) {
            alert('Помилка додавання товару до кошика');
        }
    };

    const totalPrice = hotel.price * nights * people;

    return (
        <div className="hotel-details">
            {error && <p className="error-message">{error}</p>}
            <img 
                src={require(`../../assets/images/${hotel.image}`)} 
                alt={hotel.name} 
                className="hotel-details-image"
            />
            <div className="hotel-info">
                <h2>{hotel.name}</h2>
                <p><strong>Опис:</strong> {hotel.description}</p>

                <label>
                    <span>Кількість ночей:</span>
                    <select className="ItemsFilter" value={nights} onChange={handleNightsChange}>
                        {[...Array(hotel.maxNights).keys()].map((i) => (
                            <option key={i + 1} value={i + 1}>{i + 1}</option>
                        ))}
                    </select>
                </label>

                <label>
                    <span>Кількість осіб:</span>
                    <select className="ItemsFilter" value={people} onChange={handlePeopleChange}>
                        {[...Array(hotel.maxPeople).keys()].map((i) => (
                            <option key={i + 1} value={i + 1}>{i + 1}</option>
                        ))}
                    </select>
                </label>
            </div>

            <p className="price">Загальна Вартість: {totalPrice} грн</p>
            <div className="buttons">
                <button className="go-back-button" onClick={() => navigate('/catalog')}>Повернутись</button>
                <button 
                    className="add-to-cart-button" 
                    onClick={handleAddToCart} 
                    disabled={!nights || !people} 
                >
                    Додати до кошика
                </button>
            </div>
        </div>
    );
};

export default ItemPage;
