import React, { useEffect, useState } from 'react';
import { getCart } from '../../api/axiosConfig';
import CartItem from '../CartItem/CartItem';
import CartSummary from '../CartSummary/CartSummary';
const Cart = () => {
    const [cart, setCart] = useState([]);

    const loadCart = async () => {
        const cartData = await getCart();
        console.log('Отримані дані кошика:', cartData); // Перевірте дані
        setCart(cartData);
    };

    useEffect(() => {
        loadCart(); // Завантаження кошика при першому рендері
    }, []);

    return (
        <div>
            {cart.length > 0 ? (
                cart.map(item => <CartItem key={item.id} item={item} onCartUpdate={loadCart}  />)
            ) : (
                <p>Кошик порожній</p>
            )}
            <CartSummary cart={cart} />
        </div>
    );
};

export default Cart;
