import React, { useState, useEffect } from 'react';
import './Grid.css'; 
import HotelCard from './HotelCard/HotelCard';
import { getProducts } from '../../api/axiosConfig';
import Loader from '../Loader/Loader';

function Grid() {
    const [hotels, setHotels] = useState([]);
    const [showAll, setShowAll] = useState(false);
    const [loading, setLoading] = useState(true); // Початкове значення true для показу Loader зразу
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchHotels = async () => {
            setLoading(true); // Початок завантаження, показ Loader
            try {
                const response = await getProducts(); // Виконання запиту до API
                setHotels(response.data); // Збереження отриманих даних
                setError(null); // Скидання помилки, якщо запит вдався
            } catch (error) {
                setError(error.message); // Збереження повідомлення про помилку
            } finally {
                setLoading(false); // Завершення завантаження, приховати Loader
            }
        };

        fetchHotels(); // Виклик функції для отримання даних при першому рендері
    }, []);

    const visibleHotels = showAll ? hotels : hotels.slice(0, 4);

    const handleToggle = () => {
        setShowAll(!showAll);
    };

    if (loading) return <Loader />; // Відображення Loader під час завантаження
    if (error) return <div>Error: {error}</div>; // Відображення помилки, якщо вона є

    return (
        <div className="grid">
            {visibleHotels.map((hotel) => (
                <HotelCard
                    key={hotel.id}
                    name={hotel.name}
                    image={hotel.image}
                    description={hotel.description}
                />
            ))}
            <button className="view-more" onClick={handleToggle}>
                {showAll ? 'Приховати' : 'Переглянути всі'}
            </button>
        </div>
    );
}

export default Grid;
