import React, { useState } from 'react';
import { updateCartItemDetails } from '../../api/cartAPI';

const CartItem = ({ item, onCartUpdate }) => {
    const [nights, setNights] = useState(item.nights);
    const [people, setPeople] = useState(item.people);

    // Оновлення кількості ночей
    const handleNightsChange = async (newNights) => {
        setNights(newNights);
        await updateCartItemDetails(item.id, newNights, people); // Надсилаємо зміни на сервер
        onCartUpdate(); // Оновлюємо кошик після зміни
    };

    // Оновлення кількості людей
    const handlePeopleChange = async (newPeople) => {
        setPeople(newPeople);
        await updateCartItemDetails(item.id, nights, newPeople); // Надсилаємо зміни на сервер
        onCartUpdate(); // Оновлюємо кошик після зміни
    };

    return (
        <div className="cart-item">
            <h3>{item.name}</h3>
            <p>Ціна за добу: {item.price} грн</p>

            <div className="quantity-controls">
                <label>
                    Ночі:
                    <input
                        type="number"
                        value={nights}
                        min="1"
                        onChange={(e) => handleNightsChange(Number(e.target.value))}
                    />
                </label>
                <label>
                    Люди:
                    <input
                        type="number"
                        value={people}
                        min="1"
                        onChange={(e) => handlePeopleChange(Number(e.target.value))}
                    />
                </label>
            </div>

            <p>Загальна сума: {item.price * nights * people} грн</p>
        </div>
    );
};

export default CartItem;
