import React, { useState, useEffect } from 'react';
import HotelCard from './HotelCard/HotelCard';
import Filters from '../Filters/Filters';
import Loader from '../Loader/Loader'; // Імпортуємо компонент Loader
import { getProducts } from '../../api/axiosConfig';
import './HotelsList.css';

function HotelsList() {
    const [hotels, setHotels] = useState([]); // Початкові дані (всі готелі)
    const [filters, setFilters] = useState({ price: '', visitors: '', rooms: '' });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Функція для завантаження готелів з фільтрами
    const fetchHotels = async (filters) => {
        setLoading(true);
        try {
            const response = await getProducts(filters); 

            setHotels(response.data); // Збереження отриманих даних
            setLoading(false);
        } catch (error) {
            setError(error.message); // Обробка помилок
            setLoading(false);
        }
    };

    // Викликаємо `fetchHotels` при зміні фільтрів
    useEffect(() => {
        fetchHotels(filters); // Передаємо фільтри у запит
    }, [filters]);

    // Функція для обробки змін у фільтрах
    const handleFilterChange = (newFilters) => {
        setFilters(newFilters);
    };

    return (
        <div>
            <Filters onFilterChange={handleFilterChange} />
            {loading ? (
                <Loader /> // Відображення завантаження
            ) : error ? (
                <div>Error: {error}</div> // Відображення помилки
            ) : (
                <div className="hotels-list">
                    {hotels.length > 0 ? (
                        hotels.map((hotel) => (
                            <HotelCard
                                key={hotel.id}
                                name={hotel.name}
                                price={hotel.price}
                                rooms={hotel.rooms}
                                visitors={hotel.visitors}
                                image={hotel.image}
                                description={hotel.description}
                            />
                        ))
                    ) : (
                        <div>Готель не знайдено</div>
                    )}
                </div>
            )}
        </div>
    );
}

export default HotelsList;
