import React, { useState, useEffect } from 'react';
import './Grid.css'; 
import hotelsData from '../../data/Hotels.json';
import HotelCard from '../HotelsList/HotelCard/HotelCard';
function Grid() {
    const [hotels, setHotels] = useState([]);
    const [showAll, setShowAll] = useState(false);

    useEffect(() => {
        setHotels(hotelsData);
    }, []);

    const visibleHotels = showAll ? hotels : hotels.slice(0, 4);

    const handleToggle = () => {
        setShowAll(!showAll);
    };

    return (
        <div className="grid">
            {hotels.map((hotel, index) => (
                <HotelCard
                    key={hotel.id}
                    name={hotel.name}
                    visitors={hotel.visitors}
                    rooms={hotel.rooms}
                    image={hotel.image}
                    price={hotel.price}
                    description={hotel.description}
                />
            ))}
            <button className="view-more">Переглянути всі</button>
        </div>
    );
}

export default Grid;
