import React from 'react';
import './Grid.css'; // Файл стилів

const Grid = () => {
    return (
        <div className="grid-container">
            <div className="grid-item">
                <img src="https://via.placeholder.com/150" alt="Tile 1" />
                <h3>Готель Rixos</h3>
                <p>розкішний курорт, відомий своїм винятковим сервісом, сучасними зручностями та неперевершеною гостинністю. Він пропонує ідеальні умови для відпочинку на узбережжі з чудовими ресторанами, спа та розвагами для всієї родини.</p>
            </div>
            <div className="grid-item">
                <img src="https://via.placeholder.com/150" alt="Tile 2" />
                <h3>Готель Hilton</h3>
                <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nunc maximus, nulla ut commodo sagittis, sapien dui mattis dui, non pulvinar lorem felis nec erat.</p>
            </div>
            <div className="grid-item">
                <img src="https://via.placeholder.com/150" alt="Tile 3" />
                <h3>Готель Буковель</h3>
                <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nunc maximus, nulla ut commodo sagittis, sapien dui mattis dui, non pulvinar lorem felis nec erat.</p>
            </div>
            <button className="view-more">Переглянути всі</button>
        </div>
    );
};

export default Grid;
