import React from "react";
import './Info.css';
import hotelsData from '../../data/Hotels.json';
import { useParams } from 'react-router-dom';
const ItemPage = () => {
    const { id } = useParams();
    const hotel = hotelsData.find((hotel) => hotel.id === parseInt(id));

    if (!hotel) {
        return <div>Готель не знайдено.</div>;
    }

    return (
        <div className="hotel-details">
            <img 
                src={require(`../../assets/images/${hotel.image}`)} 
                alt={hotel.name} 
                className="hotel-details-image"
            />
            <h2>{hotel.name}</h2>
            <p><strong>Опис:</strong> {hotel.description}</p>
            <p><strong className="visitors">Кількість відвідувачів:</strong> {hotel.visitors}</p>
            <p><strong>Кількість номерів:</strong> {hotel.rooms}</p>
            <p><strong>Ціна:</strong> {hotel.price} грн / за добу</p>
        </div>
    );
};

export default ItemPage;
